import React, { useState, useEffect } from "react";
import Logo from "../assets/TravelLogo.png";
import { Link } from "react-router-dom";
import Login from "../pages/Login.js";

import "../styles/Navbar.css";

function Navbar(props) {
  const [openLinks, setOpenLinks] = useState(false);
  const storedUser = JSON.parse(localStorage.getItem("user"));
  const [userName, setUserName] = useState('');
  console.log(username);
  const toggleNavbar = () => {
    setOpenLinks(!openLinks);
  };

  useEffect(() => {
    async function getUsername() {
      try {
        const response = await fetch(`http:localhost/8800/api/users/${storedUser._id}`);
        const data = await response.json();
        setUserName(data.username);
      } catch (error) {
        console.log(error);
        setUserName(null); // or you can set a default value here like "Login"
      }
    }
    getUsername();
  }, []);


  return (
    <div className="navbar">
      <div className="leftSide" id={openLinks ? "open" : "close"}>
       <Link to="/"><img src={Logo} alt='xdxd'/></Link> 
        <div className="hiddenLinks">
          <Link to="/"> Home </Link>
          <Link to="/destinations"> Destinations </Link>
          <Link to="/about"> About </Link>
          <Link to="/login"> Log in </Link>
        </div>
      </div>
      <div className="rightSide">
        <Link to="/"> Home </Link>
        <Link to="/destinations"> Destinations </Link>
        <Link to="/about"> About </Link>
         <Link to="/login">{username ? <span>{username}</span> : "Login" }</Link>
        <button onClick={toggleNavbar}>

        </button>
      </div>
    </div>
  );
}

export default Navbar;
