import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import useFetch from "../hooks/useFetch";




const List = () => {
    const { name }= useParams();
    const { data,loading,error } = useFetch(`http://localhost:8800/api/hotels//finda/:area=${name}`);
    const [hotels, setHotels] = useState(null);
    useEffect(()=> {
        fetch(`http://localhost:8800/api/hotels//finda/:area=${name}`)
        .then(response => response.json())
        .then(data => setHotels(data.message))
    })
    return (
        <div>
          <h1>Hotels in {name}</h1>
          {data.map((hotel) => (
            <div key={hotel._id}>
              <h2>{hotel.name}</h2>
              <p>Location: {hotel.location}</p>
              <p>Distance: {hotel.distance}</p>
              <img src={hotel.photos} alt={hotel.name} />
              <p>Rating: {hotel.rating}</p>
              <p>Cheapest Price: {hotel.cheapestPrice}</p>
            </div>
          ))}
        </div>
      );
    }
export default List;
