import React from 'react';
import { useParams } from 'react-router-dom';
import useFetch from '../hooks/useFetch';
import "../styles/Hotels.css";
import Kumrat from "../assets/Kumrat.jpg"

function Hotels() {
  const { name } = useParams();
  const { data, loading, error } = useFetch(`http://localhost:8800/api/hotels/finda/${name}`);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error fetching data</div>;
  }

  return (
    <div className="body" >
      <h1>Hotels in {name}</h1>
      <div className="ccontainer">{data.map((hotel) => (
        <div key={hotel._id}>
           <div className="item1"><div className="ccontainer2"><button className="buttonxd">hh</button><img className="imgh" src={Kumrat} alt={hotel.name} /><button className="buttonxd">hh</button>
           <div className="item1"><p>Rating: {hotel.rating}</p>
           </div>
           <div>
          <h1 className="item2">{hotel.name}</h1>
          
          <p className="item3">Location: {hotel.location}</p>
          <p className="item3">Distance from center: {hotel.distance}</p>
          <p className="item3">Cheapest Price: {hotel.cheapestPrice}</p></div>
          </div></div>
          
        </div>
      ))}</div>
    </div>
  );
}

export default Hotels;
