import React from 'react';
import {SliderData }from "../helpers/SliderData.js";

const Locations = (props) => {
  const destinationId = props.match.params.id;
  const destination = SliderData.find(destination => destination.id === parseInt(destinationId));
  const images = destination ? destination.images : [];

  return (
    <div>
      {/* Destination Detail Information */}

      <h3>Image Slider</h3>
      {images.map((image, index) => (
        <div key={index}>
          <img src={image.url} alt={image.description} />
          <p>{image.description}</p>
        </div>
      ))}
    </div>
  );
};

export default Locations;
