import React from 'react';
import Slider from 'react-slick';
import { DestList } from "../helpers/DestList";

function ImageSlider({ images }) {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };
  return (
    <Slider {...settings}>
      {images.map((image, index) => (
        <div key={index}>
          <img src={image} alt="" />
        </div>
      ))}
    </Slider>
  );
}

function Locations({ match }) {
  const { id } = match.params;
  const dest = DestList.find(destination => destination.id === id);
  return (
    <div className="destinationDetail">
      <h1>{dest.name}</h1>
      <ImageSlider images={dest.images} />
      <p>{dest.description}</p>
    </div>
  );
}
export default Locations;
