import React from "react";
import { useParams } from "react-router-dom";
import Skardu from "../components/Skardu";
import Swat from "../components/Swat";
import Hunza from "../components/Hunza";
import useFetch from "../hooks/useFetch";
import "../styles/ImageSlider.css";

function Destination(props) {
  const { name } = useParams();
  
  const { data,loading,error } = useFetch(`http://localhost:8800/api/hotels/countByArea?areas=${name}`);
  console.log(data);

  switch (name) {
    case "swat":
      return (<div><div className="left-column">
        <Swat />
        </div> 
        <div className="right-column"><button className="right-column">{data} hotels</button></div>
        </div>);
    case "skardu":
      return (<div><div className="left-column">
      <Skardu />
      </div> 
      <div className="right-column"><button className="right-column">{data} hotels</button></div>
      </div>);
    case "hunza":
      return (<div><div className="left-column">
      <Hunza />
      </div> 
      <div className="right-column"><button>{data} hotels</button></div>
      </div>);
    default:
      return <div>Destination not found</div>;
  }
}

export default Destination;
