import React from 'react';
import { useParams } from 'react-router-dom';
import useFetch from '../hooks/useFetch';
import "../styles/Hotels.css";
import Kumrat from "../assets/Kumrat.jpg"

function Hotels() {
  const { name } = useParams();
  const { data, loading, error } = useFetch(`http://localhost:8800/api/hotels/finda/${name}`);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error fetching data</div>;
  }

  return (
    <div className="body" >
      <h1>Hotels in {name}</h1>
      <div className="hotelList">{data.map((hotel) => (
        <div key={hotel._id}>
           <div className="rowFlex"><button className="buttonxd">hh</button><img className="imgh" src={Kumrat} alt={hotel.name} /><button className="buttonxd">hh</button>
           <div>
          <h1>{hotel.name}</h1>
          <div className="text"><p>Rating: {hotel.rating}</p>
          <p>Location: {hotel.location}</p>
          <p>Distance from center: {hotel.distance}</p>
          <p>Cheapest Price: {hotel.cheapestPrice}</p></div>
          </div></div>
          
        </div>
      ))}</div>
    </div>
  );
}

export default Hotels;
