import React, {useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import ContactLeft from "../assets/Contact.jpg";
import "../styles/Contact.css";
import styles from '../styles/Valid.module.css';
import Cookies from 'js-cookie';

function getCookie(name) {
  const value = `; ${document.cookie}`;
  const parts = value.split(`; ${name}=`);
  if (parts.length === 2) {
    return parts.pop().split(";").shift();
  }
}

function Login() {
  const [enteredEmail, setEnteredEmail] = useState('');
  const [enteredPassword, setEnteredPassword] = useState('');
  const [isValid, setIsValid] = useState(true);
  const navigate = useNavigate();
  const [userName, setUserName] = useState('');
  const [accessToken, setAccessToken] = useState("");

  const emailInputChangeHandler = event => {
    if(event.target.value.trim().length>0) {
      setIsValid(true);
    }
    setEnteredEmail(event.target.value);

  };

  const passwordInputChangeHandler = event => {
    if(event.target.value.trim().length>0) {
      setIsValid(true);
    }
    setEnteredPassword(event.target.value);

  };

  const formSubmitHandler = async (event) => {
    event.preventDefault();
    if (enteredEmail.trim().length && enteredPassword.trim().length === 0) {
      setIsValid(false);
      return;
    }


    try {
      const response = await fetch("http://localhost:8800/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: enteredEmail,
          password: enteredPassword // replace with the actual password entered by the user
        }),
      });
      useEffect(() => {
        const token = getCookie("access_token");
        setAccessToken(token);
      }, []);

      const responseData = await response.json();
      if (response.ok) {

        // login successful, store the token in local storage or a cookie
        console.log(responseData.email);
        localStorage.setItem("token", accessToken);
         // get the user's name from the API and store it in state
         const userResponse = await fetch("http://localhost:8800/api/users", {
          method: "GET",
          headers: {
            "Authorization": "Bearer: " + responseData.token,
            "Content-Type": "application/json",
          }
        });
        const userResponseData = await userResponse.json();
        setUserName(userResponseData.name);
        // redirect to the protected route

      } else {
        // login failed, display an error message
        alert(responseData.message);
      }
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div className="contact">
      <div
        className="leftSide"
        style={{ backgroundImage: `url(${ContactLeft})` }}
      ></div>
      <div className="rightSide">
        <h1> Login</h1>

        <form onSubmit={formSubmitHandler} id="contact-form" method="POST">
          <div className={`${styles['form-control']} ${!isValid ? styles.invalid : styles.valid}`}>
          <label htmlFor="email">Email</label>
          <input name="email" placeholder="Enter your email" type="email" onChange={emailInputChangeHandler} />
          <label htmlFor="password">Password</label>
          <input name="password" placeholder="Enter password..." type="password" onChange={passwordInputChangeHandler} />
          <button type="submit"> Login</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
