import React from "react";
import {sliderData} from "../helpers/SliderData";

function Locations({destinationId}) {
  const destination = sliderData.find(destination => destination.id === destinationId);

  return (
    <div>"Please work"
      {destination && destination.images.map((image, index) => (
        <div key={index}>
          <img src={image.image1} alt={image.description} />
          <p>{image.description}</p>
        </div>
      ))}
    </div>
  );
}

export default Locations;
