import React, {useState} from "react";
import ContactLeft from "../assets/Contact.jpg";
import "../styles/Contact.css";
import styles from '../styles/Valid.module.css';

function Login() {
  const [enteredValue, setEnteredValue] = useState('');
  const [isValid, setIsValid] = useState(true);

  const goalInputChangeHandler = event => {
    if(event.target.value.trim().length>0) {
      setIsValid(true);
    }
    setEnteredValue(event.target.value);

  };

  const formSubmitHandler = async (event) => {
    event.preventDefault();
    if (enteredValue.trim().length === 0) {
      setIsValid(false);
      return;
    }

    try {
      const response = await fetch("http://localhost:8800/api/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: enteredValue,
          password: enteredValue // replace with the actual password entered by the user
        }),
      });

      const responseData = await response.json();
      if (response.ok) {
        // login successful, store the token in local storage or a cookie
        localStorage.setItem("token", responseData.token);
        // redirect to the protected route
        window.location.href = "/protected-route";
      } else {
        // login failed, display an error message
        alert(responseData.message);
      }
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div className="contact">
      <div
        className="leftSide"
        style={{ backgroundImage: `url(${ContactLeft})` }}
      ></div>
      <div className="rightSide">
        <h1> Login</h1>

        <form onSubmit={formSubmitHandler} id="contact-form" method="POST">
          <div className={`${styles['form-control']} ${!isValid ? styles.invalid : styles.valid}`}>
          <label htmlFor="email">Email</label>
          <input name="email" placeholder="Enter your email" type="email" onChange={goalInputChangeHandler} />
          <label htmlFor="password">Password</label>
          <input name="password" placeholder="Enter password..." type="password" onChange={goalInputChangeHandler} />
          <button type="submit"> Login</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
