import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ImageSlider from './ImageSlider';

function Locations() {
  let { id } = useParams();

  useEffect(() => {
    console.log('ID:', id);
  }, [id]);

  return (
    <div>
      <h1>Image Slider</h1>
      <ImageSlider id={id} />
    </div>
  );
}

export default Locations;
